function ShaderFuse.end_create()

    local version_info=''

    if ShaderFuse.Fuse.Version =='' and ShaderFuse.Fuse.Date == '' then
      version_info=''
    elseif ShaderFuse.Fuse.Version ~='' and ShaderFuse.Fuse.Date == '' then
      version_info='Fuse version '.. ShaderFuse.Fuse.Version ..'<br />'
    elseif ShaderFuse.Fuse.Version =='' and ShaderFuse.Fuse.Date ~= '' then
      version_info=ShaderFuse.Fuse.Date ..'<br />'
    elseif ShaderFuse.Fuse.Version ~='' and ShaderFuse.Fuse.Date ~= '' then
      version_info='Version '.. ShaderFuse.Fuse.Version ..' - '.. ShaderFuse.Fuse.Date ..'<br />'
    end


    local attr = {
        ICS_ControlPage = 'Info',
        IC_ControlPage = 1,
        LINKID_DataType = "Text",
        INPID_InputControl = "LabelControl",
        LBLC_MultiLine = true,
        IC_NoLabel = true,
        IC_NoReset = true,
        INP_External = false,
        INP_Passive = true
    }

    if version_info ~= '' then
        version_info = '<br /><span style="color:#4060a0; ">'.. version_info ..'</span>'
    end

    self:AddInput('<br /><p align="center">Shadertoy<br /><a href="https://www.shadertoy.com/view/'.. ShaderFuse.Shadertoy.ID ..'" style="color:white; text-decoration:none; font-size:x-large; ">'.. ShaderFuse.Shadertoy.Name ..'</a><br />by <a href="https://www.shadertoy.com/user/'.. ShaderFuse.Shadertoy.Author ..'" style="color:#a0a060; text-decoration:none; ">'.. ShaderFuse.Shadertoy.Author ..'</a><br /><span style="color:#a06060; ">'.. ShaderFuse.Shadertoy.License ..'</span></p><p align="center">DCTLified and DaFused by <a href="'.. (ShaderFuse.Fuse.AuthorURL == '' and "https://nmbr73.github.io/Shadertoys/" or ShaderFuse.Fuse.AuthorURL) ..'" style="color:#a0a060; text-decoration:none; ">'.. ShaderFuse.Fuse.Author ..'</a>'.. version_info ..'</p>', "DctlFuseInfoText", attr )
    self:AddInput( ShaderFuse.Fuse.Name.." Info ...", "DctlFuseInfoFuseButton", { ICS_ControlPage = 'Info', IC_ControlPage = 1, INPID_InputControl = "ButtonControl", INP_DoNotifyChanged = false, INP_External = false, BTNCS_Execute = 'bmd.openurl("'.. ShaderFuse.Fuse.InfoURL ..'")' })
    self:AddInput( ShaderFuse.Shadertoy.Name.." Shadertoy ...", "DctlFuseInfoShadertoyButton", { ICS_ControlPage = 'Info', IC_ControlPage = 1, INPID_InputControl = "ButtonControl", INP_DoNotifyChanged = false, INP_External = false, BTNCS_Execute = 'bmd.openurl("'.. ShaderFuse.Shadertoy.InfoURL ..'")' })
    self:AddInput('<br /><p align="center"><img width="320" height="180" src="data:image/png;base64," /></p>', "DctlFuseInfoLabelThumbnail", attr )

    if ShaderFuse.Fuse.AuthorLogo then
        self:AddInput('<br /><p align="right"><a href="https://github.com/nmbr73/Shadertoys"><img width="'.. ShaderFuse.Fuse.AuthorLogo.Width ..'" height="'.. ShaderFuse.Fuse.AuthorLogo.Height ..'" src="data:image/png;base64,'.. ShaderFuse.Fuse.AuthorLogo.Data ..'" /></a></p>', "DctlFuseLogoLabel", attr)

        -- self:AddInput('<br /><p align="right"><a href="https://github.com/nmbr73/Shadertoys"><img '.. ShaderFuse.Fuse.AuthorLogo ..' /></a></p>', "DctlFuseLogoLabel", attr)
    end

    self:AddInput('<p>This version of the Fuse seems to come straight from the repository. It should thereby represent the lateste development snapshot - that is as long as you keep your working copy up to date. This type of installation - if done right - should allow you to easily test your own changes localy before contributing them to the origin.</p>', "DctlFuseInfoDescription", attr )

    -- ..'Reactor package v'..SHADERFUSES_REACTOR_PACKAGE_VERSION..' created '..SHADERFUSES_REACTOR_PACKAGE_DATE..'<br />'

end



function ShaderFuse.assert(condition, message)

    if condition then
        return
    end

    if ShaderFuse.noAssert then

        if not ShaderFuse.error then
            ShaderFuse.error = message
        end

        return
    end

    assert(false, message)
end




