local logo_data = ''

local path_separator = package.config:sub(1,1)
local shaderfuses_path = fusion:MapPath("Fuses:/Shaderfuse_wsl/")

local files = bmd.readdir(shaderfuses_path.. "*.fuse")
local fuses = {}

for _, file in ipairs(files) do

	local handle = io.open(shaderfuses_path .. file.Name,"r")

  if handle ~= nil then

    local line = handle:read("*l")
    handle:close()

    _, _, version, compatibility, name, author, id, port, category = string.find(line, "%-%- MAGIC%-A|(v%d%.%d)|(%d+)|\tNAME: ([^\t]+)\tAUTHOR: ([^\t]+)\tID: ([^\t]+)\tPORT: ([^\t]+)\tCATEGORY: ([^\t]+)\t")

    if id ~= nil then
      table.insert(fuses,{
        Compatibility = compatibility,
        Name = name,
        Author = author,
        Id = id,
        Port = port,
        Category = category,
        Version = version,
      })
    end
  end
end






local ui = fu.UIManager
local ui_dispatcher = bmd.UIDispatcher(ui)

local thumbWidth = 320
local thumbHeight = 180

local win = ui_dispatcher:AddWindow({

    ID = "ShaderfuseOverview",
    WindowTitle = "Shaderfuses Overview",
    Geometry = { 100, 100, 700, 600 },

    ui:VGroup {

      ui:HGroup {
        Weight = 0,
        ui:VGroup {
          ui:Label{
            ID = "",
            WordWrap = false,
            Weight = 0,
            MinimumSize = { 274, 63 },
            ReadOnly = true,
            Flat = true,
            Alignment = { AlignHCenter = false, AlignTop = true, },
            Text = '<img src="data:image/png;base64," width="274" height="63" />',
          },

          ui:Label {
            ID = 'Help',
            Text = "&nbsp;<br>Button 'Add' to add node to comp.<br>Double click on item for info.<br>'Close' to close this window.",
            Weight = 3.0,
            Alignment = { AlignHCenter = false, AlignVTop = true, },
            WordWrap = false,
          },
        },
        ui:HGap(0,1),

        ui:Label{
          Weight = 0,
          ID = "Thumbnail",
          MinimumSize = {thumbWidth, thumbHeight},
          Alignment = { AlignHCenter = false, AlignTop = true, },
          WordWrap = false, ReadOnly = true, Flat = true, Text = '',
        },
      },

      ui:VGap(5),

      ui:Tree {
        ID = 'Files',
        Weight = 2,
        MinimumSize = {600, 200},
        SortingEnabled=true, -- UpdatesEnabled=true,
        Events = { ItemDoubleClicked=true, CurrentItemChanged = true, },
      },

      ui:VGap(5),

      ui:HGroup{
        Weight = 0,

        ui:Label {
          ID = 'Info',
          Weight = 3.0,
          Alignment = { AlignHCenter = false, AlignVTop = true, },
          WordWrap = false,
        },

        ui:HGap(0,1),
        ui:Button{ ID = "Add", Text = "Add", Enabled = false },
        ui:Button{ ID = "Cancel", Text = "Close" },
      },

    },
  })


local itm = win:GetItems()
local current_fuse = nil

function win.On.Add.Clicked(ev)
	-- win:Hide()
  local fuse = current_fuse
  if fuse and fuse ~= "" and comp then
    comp:AddTool('Fuse.Shaderfuse_' .. tostring(fuse) .. '_reactor', -32768, -32768)
  end
end


function win.On.Cancel.Clicked(ev)
    win:Hide()
    ui_dispatcher:ExitLoop()
end


function win.On.ShaderfuseOverview.Close(ev)
    win:Hide()
    ui_dispatcher:ExitLoop()
end

function win.On.Files.ItemDoubleClicked(ev)
    local category = ev.item.Text[0]
    local name = ev.item.Text[1]

    if name ~= nil and category ~= nil then
      bmd.openurl('https://nmbr73.github.io/Shaderfuse/'..category..'/'..name..'/')
    end
end


function win.On.Files.CurrentItemChanged(ev)
    local fuse=ev.item.Text[2]
    current_fuse = fuse
    if fuse==nil then
      itm.Thumbnail.Text=''
      itm.Add.Enabled = false
      itm.Info.Text = ''
      return
    end

    itm.Add.Enabled = (comp ~= nil)

    itm.Thumbnail.Text='<img src="' .. shaderfuses_path .. path_separator .. fuse
      .. '.png" width="'..thumbWidth..'" height="'..thumbHeight..'" />'

    itm.Info.Text = '  '..ev.item.Text[1]..' ported by '..ev.item.Text[4]
end



local hdr = itm.Files:NewItem()

hdr.Text[0] = 'Category'
hdr.Text[1] = 'Fuse Name'
hdr.Text[2] = 'Toy ID'
hdr.Text[3] = 'Author'
hdr.Text[4] = 'Port'
hdr.Text[5] = 'Status'

itm.Files:SetHeaderItem(hdr)
itm.Files.ColumnCount = 5

itm.Files.ColumnWidth[0] = 160
itm.Files.ColumnWidth[1] = 380
itm.Files.ColumnWidth[2] = 60
itm.Files.ColumnWidth[3] = 100
itm.Files.ColumnWidth[4] = 60
itm.Files.ColumnWidth[5] = 60

local numFuses=0

for _, f in ipairs(fuses) do

    local newitem = itm.Files:NewItem()

    newitem.Text[0] = f.Category
    newitem.Text[1] = f.Name
    newitem.Text[2] = f.Id
    newitem.Text[3] = f.Author
    newitem.Text[4] = f.Port
    newitem.Text[5] = "?"

    itm.Files:AddTopLevelItem(newitem)

	numFuses=numFuses+1
end

itm.Files:SortByColumn(1, "AscendingOrder")
itm.Files:SortByColumn(0, "AscendingOrder")



win:Show()
ui_dispatcher:RunLoop()
win:Hide()

